import React from 'react';
import 'tachyons';

class Register extends React.Component {

	constructor(props) {
		super(props)
		this.state = {
			name : '',
			email : '',
			password : '',
			userAlreadyPresent : ''
		}
	}

	onEmailChange = (event) => {
		this.setState( {
			email : event.target.value
		})
	}

	onPasswordChange = (event) => {
		this.setState( {
			password : event.target.value
		})
	}

	onNameChange = (event) => {
		this.setState( {
			name : event.target.value
		})
	}

	onRegister = () => {
		fetch('https://peaceful-journey-85632.herokuapp.com/register' , {
			method : 'POST',
			headers : { 'Content-Type' : 'application/json'},
			body : JSON.stringify({
				name : this.state.name,
				email : this.state.email,
				password : this.state.password
			})
		})
		.then(response => response.json())
		.then( user => {
			if(user.id) {
				this.props.loadUser(user);
				this.props.onRouteChange('home');
			}
			else {
				this.setState({
					userAlreadyPresent : 'User already registered'
				})
			}
		})
	}

	render() {
		return(
		<article className="br2 shadow-5 ba dark-gray b--black-10 mv4 w-100 w-50-m w-25-l mw9 center">
			<main className="pa4 black-80 ">
			  <div className="measure">
				    <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
				      <legend className="f4 fw6 ph0 mh0 center">Register</legend>
				      <div className="mt3">
				        <label className="db fw6 lh-copy f6" htmlFor="name">Name</label>
				        <input onChange={this.onNameChange} className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="text" name="name"  id="name" />
				      </div>
				      <div className="mt3">
				        <label className="db fw6 lh-copy f6" htmlFor="email-address">Email</label>
				        <input onChange={this.onEmailChange} className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="email" name="email-address"  id="email-address" />
				      </div>
				      <div className="mv3">
				        <label className="db fw6 lh-copy f6" htmlFor="password">Password</label>
				        <input onChange={this.onPasswordChange} className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="password" name="password"  id="password" />
				      </div>
				    </fieldset>
				    <div className="center red mb2">
					    {this.state.userAlreadyPresent}
					</div>
				    <div className="center">
				      <input onClick={this.onRegister}className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" type="submit" value="Sign up" />
				    </div>
			  </div>
			</main>
		</article>
	)
	}
}

export default Register;
